import { useParams } from 'react-router-dom';

type Recipe = {
  title: string;
  prepTime: string;
  ingredients?: string[];
  instructions?: string;
  videoUrl?: string;
  steps?: { text: string; image: string }[];
};

const mockRecipes: Record<string, Recipe> = {
  '1': {
    title: 'Spaghetti Bolognese',
    prepTime: '30 min',
    videoUrl: 'https://www.youtube.com/watch?v=v2WqcHH65NQ',
    ingredients: ['pasta', 'tomato', 'ground beef', 'garlic', 'onion'],
    steps: [
      {
        text: 'Boil pasta until al dente.',
        image: 'data:image/jpeg;base64,'
      },
      {
        text: 'Cook ground beef with garlic and onion.',
        image: 'data:image/jpeg;base64,'
      },
      {
        text: 'Add tomato sauce and simmer.',
        image: 'data:image/jpeg;base64,'
      }
    ]
  },
  '2': {
    title: 'Tofu Stir-Fry',
    prepTime: '20 min',
    videoUrl: 'https://www.youtube.com/watch?v=Ecq2svvSWXc',
    ingredients: ['tofu', 'soy sauce', 'vegetables', 'garlic'],
    steps: [
        {
            text: 'Fry tofu',
            image: 'data:image/jpeg;base64,'
        },
        {
            text: 'Add veggies and sauce',
            image: 'data:image/jpeg;base64,'
        },
        {
            text: 'Cook until done',
            image: 'data:image/jpeg;base64,'
        }
    ]
  },
  '3': {
    title: 'Chicken Curry',
    prepTime: '40 min',
    videoUrl: 'https://www.youtube.com/watch?v=HRKCWPY-538',
    ingredients: ['chicken', 'curry paste', 'coconut milk', 'onion'],
    steps: [
        {
            text: 'Cook chicken with curry paste and onion',
            image: 'data:image/jpeg;base64,'
        },
        {
            text: 'add coconut milk',
            image: 'https://kalejunkie.com/wp-content/uploads/2024/03/KJ_One-Pan-Saucy-Coconut-Chicken-6.jpg'
        },
        {
            text: 'simmer',
            image: 'data:image/jpeg;base64,'
        }
    ]
  }
};

export default function RecipeDetail() {
  const { id } = useParams<{ id: string }>();
  const recipe = mockRecipes[id as keyof typeof mockRecipes];

  if (!recipe) {
    return <p>Recipe not found.</p>;
  }

  return (
    <main>
      <h1>{recipe.title}</h1>
      <p><strong>Prep Time:</strong> {recipe.prepTime}</p>
      {recipe.ingredients && (
        <p><strong>Ingredients:</strong> {recipe.ingredients.join(', ')}</p>
      )}
      {recipe.videoUrl && (
        <section>
          <iframe
            width="100%"
            height="315"
            src={recipe.videoUrl.replace('watch?v=', 'embed/')}
            title="Recipe video"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </section>
      )}
      {recipe.steps && (
        <section>
          <h2>Instructions</h2>
          {recipe.steps.map((step, index) => (
            <div key={index} style={{ marginBottom: '1rem' }}>
              <img src={step.image} alt={`Step ${index + 1}`} style={{ width: '100%', maxWidth: '500px' }} />
              <p>{index + 1}. {step.text}</p>
            </div>
          ))}
        </section>
      )}
      {recipe.instructions && !recipe.steps && (
        <section>
          <h2>Instructions</h2>
          <p>{recipe.instructions}</p>
        </section>
      )}
    </main>
  );
}
